import React, {useState} from 'react';

class ShoppingList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          milk: 10,
          biscuits: 15,
          fruit_juice : 12,
        };

      }

      render() {
        return (
          <div>
            <p>You clicked {this.state.milk} times</p>
            <button onClick={() => this.setState({ milk: this.state.milk + 1 })}>
              Click me
            </button>
          </div>
        );
      }
}
